import "../css/textInput.css"

const TextInput = (props) => {
    const { type, title, placeholder, onChange, onClick } = props

    return (
        <div className="text-input">
            <label style={
                {
                    fontSize: "18px",
                    fontWeight: "bold",
                    marginBottom: "10px",
                    marginTop: "10px",

                }
            }>{title}</label>
            <input
                type={type}
                onChange={onChange}
                onClick
                placeholder={placeholder}
            />
        </div>
    )
}

export default TextInput
