import "../css/textInput.css"

const TextInput = (props) => {
    const { type, title, placeholder, onchange } = props

    return (
        <div
            className="TextInput"
            style={
                {
                    display: "flex",
                    flexDirection: "column",
                    width: "60%",
                    margin: "0 auto"
                }
            }
        >
            <label style={
                {
                    fontSize: "18px",
                    fontWeight: "bold",
                    marginBottom: "10px",
                    marginTop: "10px",

                }
            }>{title}</label>
            <input
                type={type}
                onChange={onchange}
                placeholder={placeholder}
            />
        </div>
    )
}

export default TextInput
